function saveBase64PDF(pdfDocument) {
    return new Promise((resolve, reject) => {
        try {
            pdfMake.createPdf(pdfDocument).getBase64(base64PDF => {
                localStorage.setItem('pdfData', base64PDF);
                resolve(base64PDF);
            });
        } catch (error) {
            console.log(error)
        }
    });
}